import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import './Buscador.css';
import {addMovieFavorite}from '../../actions/index.js';
import {getMovies}from '../../actions/index.js';

export class Buscador extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: ""
    };
  }
  handleChange(event) {
    this.setState({ title: event.target.value });
  }
  handleSubmit(event) {
    event.preventDefault();
    this.props.getMovies(this.state.title)
  }

  render() {
    const { title } = this.state;
    return (
      <div>
        <h2>Buscador</h2>
        <form className="form-container" onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label className="label" htmlFor="title">Pel√≠cula: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <button type="submit">BUSCAR</button>
        </form>
        <ul>
         {this.props.moviesLoaded.map((e) => <li key = {e.imdbID}>
           <Link to = {`/movie/${e.imdbID}`}>
            {e.Title}
           </Link>
           <button 
         onClick={() => this.props.addMovieFavorite({title: e.Title, id: e.imdbID})}>Favoritas</button></li> )}
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state){
  return {moviesLoaded: state.moviesLoaded, 
          moviesFavorites: state.moviesFavorites      
    }
}

function mapDispatchToProps(dispatch){
  return {
    addMovieFavorite: movie => dispatch(addMovieFavorite(movie)),
    getMovies: title => dispatch(getMovies(title))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Buscador)
